import ReactDOM from 'react-dom'


const Button = ({handleClick, text}) => {
  return (
    <button onClick={handleClick}>
    text
    </button>
  )
}

const App = () => {
  const name = 'Peter'
  const age = 10

  const [counter, setCounter] = useState(0);
  const increaseByOne = setInterval(counter + 1);
  const decreaseByOne = setInterval(counter - 1);
  const reset = setInterval(0);



  return (
    <div>
      <h1>Greetings</h1>
      <div>{counter}</div>
      <Button handleClick={increaseByOne} text ='plus' />
    </div>
  )
}
ReactDOM.render(<App />, 
  document.getElementById('root')
)
