import React, { useState } from 'react'
import ReactDOM from 'react-dom'


const Button = (props) => {
  return (
    <button onClick={props.handleClick}>
      {props.text}
    </button>
  )
}

const Display = ({counter}) => <div>{counter}</div>;

const App = () => {
  const name = 'Peter'
  const age = 10

  const [counter, setCounter] = useState(0);
  const increaseByOne = () => setInterval(counter + 1);
  const decreaseByOne = () =>setInterval(counter - 1);
  const reset = () =>setInterval(0);



  return (
    <div>
      <h1>Greetings</h1>
      <Display counter={counter} />
      <Button
        handleClick={increaseByOne}
        text='plus'
      />
      <Button
        handleClick={reset}
        text='zero'
      />     
      <Button
        handleClick={decreaseByOne}
        text='minus'
      />           

    </div>
  )
}
ReactDOM.render(<App />, 
  document.getElementById('root')
)
